import React, { useState, useEffect } from "react";
import { SpotifyAuth, Scopes } from "react-spotify-auth";
import SpotifyWebApi from "spotify-web-api-js";

const spotifyApi = new SpotifyWebApi();

function Main() {
  const [token, setToken] = useState(null);
  const [topTracks, setTopTracks] = useState([]);
  const [albumCovers, setAlbumCovers] = useState([]);

  const onAccessToken = (accessToken) => {
    setToken(accessToken);
    spotifyApi.setAccessToken(accessToken);
  };

  useEffect(() => {
    // Get access token from Spotify
    const hash = window.location.hash
      .substring(1)
      .split("&")
      .reduce(function (initial, item) {
        if (item) {
          var parts = item.split("=");
          initial[parts[0]] = decodeURIComponent(parts[1]);
        }
        return initial;
      }, {});
    window.location.hash = "";
    let _accessToken = hash.access_token;

    if (_accessToken) {
      setAccessToken(_accessToken);
      spotifyApi.setAccessToken(_accessToken);

      // Get user's top tracks
      spotifyApi.getMyTopTracks({ limit: 24 }).then((data) => {
        setTopTracks(data.items);

        // Get album covers for each track's album
        const albumIds = data.items.map((track) => track.album.id);
        spotifyApi.getAlbums(albumIds).then((data) => {
          setAlbumCovers(data.albums.map((album) => album.images[0].url));
        });
      });
    }
  }, []);

  return (
    <div>
      {token ? (
        <h1>You are authenticated with Spotify!</h1>
      ) : (
        <SpotifyAuth
          clientID="ed8e6a84c4d94b31a47fa7f4d8e8c274"
          redirectUri="http://localhost:3000/callback"
          scopes={["streaming", "user-read-private", "user-read-email"]}
          onAccessToken={onAccessToken}
        />
      )}
    </div>
  );
}

export default Main;
